const FOLLOW = 'FOLLOW';
const UNFOLLOW = 'UNFOLLOW';
const SET_USERS = 'SET_USERS';
let initialState = {
    users: [
        {
            id: 1,
            photo_url: 'https://images-na.ssl-images-amazon.com/images/I/61FJ3DOCMhL._AC_SL1000_.jpg',
            followed: false,
            full_name: 'Yaroslav',
            status: "Must I dress out the mask..",
            location: {city: "Niz Nov", country: 'RF'}
        },
        {
            id: 2,
            photo_url: 'https://images-na.ssl-images-amazon.com/images/I/61FJ3DOCMhL._AC_SL1000_.jpg',
            followed: true,
            full_name: 'Alex',
            status: "fuck the Russia",
            location: {city: "Balashikha", country: 'RF'}
        },
        {photo_url: 'https://images-na.ssl-images-amazon.com/images/I/61FJ3DOCMhL._AC_SL1000_.jpg',id: 3, followed: false, full_name: 'Kaneki', status: "not found", location: {city: "Tokyo", country: 'Japan'}}
    ]
};

const users_reducer = (state = initialState, action) => {

    switch (action.type) {
        case FOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.user_id) {
                        return {...u, followed: true}
                    }
                    return u;
                })
            }
        case UNFOLLOW:
            return {
                ...state,
                users: state.users.map(u => {
                    if (u.id === action.user_id) {
                        return {...u, followed: false}
                    }
                    return u;
                })
            }
        case SET_USERS: {
            return {...state, users: [...state.users, ...action.users]}
        }

        default:
            return state;
    }
}


export const follow_ac = (user_id) => ({type: FOLLOW, user_id})
export const unfollow_ac = (user_id) => ({type: UNFOLLOW, user_id})
export const set_users_ac = (users) => ({type: SET_USERS, users})
export default users_reducer;
