import React from 'react';
import styles from './users.module.css';
import * as axios from "axios";

let Users = (props) => {
    if (props.users.length === 0) {
        axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {
            debugger;
                props.set_users([
                        {
                            id: 1,
                            photo_url: 'https://images-na.ssl-images-amazon.com/images/I/61FJ3DOCMhL._AC_SL1000_.jpg',
                            followed: false,
                            full_name: 'Yaroslav',
                            status: "Must I dress out the mask..",
                            location: {city: "Niz Nov", country: 'RF'}
                        },
                        {
                            id: 2,
                            photo_url: 'https://images-na.ssl-images-amazon.com/images/I/61FJ3DOCMhL._AC_SL1000_.jpg',
                            followed: true,
                            full_name: 'Alex',
                            status: "fuck the Russia",
                            location: {city: "Balashikha", country: 'RF'}
                        },
                        {
                            photo_url: 'https://images-na.ssl-images-amazon.com/images/I/61FJ3DOCMhL._AC_SL1000_.jpg',
                            id: 3,
                            followed: false,
                            full_name: 'Kaneki',
                            status: "not found",
                            location: {city: "Tokyo", country: 'Japan'}
                        }

                    ]
                )
            }
        );

    }
    return <div>
        {
            props.users.map(u => <div key={u.id}>
                <span>
                    <div>
                        <img className={styles.user_photo} src={u.photo_url}/>
                    </div>
                </span>
                    <span>
                    {u.full_name}
                        <div>{u.status}</div>
                </span>
                    <span>
                    {u.followed ? <button onClick={() => {
                            props.unfollow(u.id)
                        }}>Unfollow</button>
                        : <button onClick={() => {
                            props.follow(u.id)
                        }}>Follow</button>}
                </span>
                    <span>
                    <div>{u.location.city}</div>
                    <div>{u.location.country}</div>
                </span>
                </div>
            )
        }
    </div>
}
export default Users;
