const UPDATE_NEW_MESSAGE_BODY = 'UPDATE_NEW_MESSAGE_BODY';
const SEND_MESSAGE = 'SEND_MESSAGE';
const dialogs_reducer = (state, action) =>{
    switch (action.type){
        case UPDATE_NEW_MESSAGE_BODY:
            state.new_message_body = action.body;
            return state;
        case SEND_MESSAGE:
            let body = state.new_message_body;
            state.new_message_body = '';
            state.messages_data.push({id: 6, message: body});
            return state;
        default:
            return state;
    }

}
export const send_message_creator = () => {

    return {
        type: SEND_MESSAGE
    }
}
export const update_new_message_body_creator = (body) => {

    return {
        type: UPDATE_NEW_MESSAGE_BODY,
        body: body
    }
}
export default dialogs_reducer;
