import React from "react";
import s from './My_posts.module.css';
import My_post from "./My_post/My_post";

function My_posts() {
    let posts_data = [
        {id: 1, message: "Hello Kaneki ", like_count: 3, dislike_count: 1},
        {id: 2, message: "Bye, Feral'", like_count: 3, dislike_count: 1},

    ]
    return (<div>
        <p className={s.text}>My posts</p>
        <textarea name="" id="" cols="19" rows="7">input here</textarea>
        <button>Add a post</button>
        <button>delete a post</button>
        <My_post message={posts_data[0].message} like_count={posts_data[0].like_count} dislike_count={posts_data[0].dislike_count}/>
        <My_post message={posts_data[1].message} like_count={posts_data[1].like_count} dislike_count={posts_data[1].dislike_count}/>

    </div>)
}

export default My_posts;
