import React from "react";
import s from './Dialogs.module.css';
import {NavLink} from "react-router-dom";

function Message(props) {
    return (
        <div className={s.messages}>
            {props.message}
        </div>
    )
}


function Dialog_item(props) {
    let path = "/dialogs/" + props.id;
    return (
        <div className={s.dialogs}>
            <NavLink to={path} activeClassName={s.active}>{props.name}</NavLink><br/>
        </div>
    )
}

function Dialogs() {
    let dialogs_data = [
        {id: 1, name: "Yaroslav"},
        {id: 2, name: "Alex"},
        {id: 3, name: "Veronika"},
        {id: 4, name: "Naruto"},
        {id: 5, name: "Tanjiro"},
        {id: 6, name: "Kaneki"},
    ];
    let messages_data = [
        {id: 1, message: "heyy"},
        {id: 2, message: "no"},
        {id: 3, message: "ok"},
        {id: 4, message: "the let's go"},
        {id: 5, message: "yeah that's great"},
        {id: 6, message: "welcome amazing"},
    ]
    let dialog_elements = dialogs_data.map
    (dialog => <Dialog_item id={dialogs_data[0].id} name={dialogs_data[0].name}/>

    );
        [
    <Dialog_item id={dialogs_data[0].id} name={dialogs_data[0].name}/>,
    <Dialog_item id={dialogs_data[1].id} name={dialogs_data[1].name}/>,
    <Dialog_item id={dialogs_data[2].id} name={dialogs_data[2].name}/>,
    <Dialog_item id={dialogs_data[3].id} name={dialogs_data[3].name}/>,
    <Dialog_item id={dialogs_data[4].id} name={dialogs_data[4].name}/>,
    <Dialog_item id={dialogs_data[5].id} name={dialogs_data[5].name}/>,
    ]
    return (
        <div className={s.content}>
            <div className={s.dialogs}>
                <h1>Dialogs</h1>
                {Dialog_elements}
            </div>
            <div>
            <Message message={messages_data[0].message} />
            <Message message={messages_data[1].message} />
            <Message message={messages_data[2].message} />
            <Message message={messages_data[3].message} />
            <Message message={messages_data[4].message} />
            </div>
        </div>
    )
}

export default Dialogs;
