import React from "react";
import s from './Dialogs.module.css';
import Dialogs_item from "./Dialogs_item/Dialogs_item";
import Message from "./Message/Message";
import {send_message_creator, update_new_message_body_creator} from "../../../redux/state/dialogs_reducer";
import Dialogs from "./Dialogs";

function Dialogs_container(props) {


    let on_new_message_change = (body) => {
        props.store.dispatch(update_new_message_body_creator(body));
    }
    let on_send_message_click = () => {
        props.store.dispatch(send_message_creator());
    }
    return <Dialogs update_new_message_body={on_new_message_change}
                    send_message={on_send_message_click}
                    Dialogs_page={state}/>

}

export default Dialogs_container;
