import React from 'react';
import Users from "./Users";
import {connect} from "react-redux";
import {
    follow_ac,
    set_users_ac,
    unfollow_ac,
    set_current_page_ac,
    set_total_users_count_ac
} from "../../redux/users-reducer";

let map_state_to_props = (state) => {
    return {
        users: state.users_page.users,
        page_size: state.users_page.page_size,
        total_user_count: state.users_page.total_user_count,
        current_page: state.users_page.current_page,
    }
}
let mapDispatchToProps = (dispatch) => {
    return {
        follow: (user_id) => {
            dispatch(follow_ac(user_id));
        },
        unfollow: (user_id) => {
            dispatch(unfollow_ac(user_id));
        },
        set_users: (users) => {
            dispatch(set_users_ac(users));
        },
        set_current_page: (page_number)=>{
            dispatch(set_current_page_ac(page_number))
        },
        set_total_users_count: (total_count)=>{
            dispatch(set_total_users_count_ac(total_count))
        }
    }

}
export default connect(map_state_to_props, mapDispatchToProps)(Users);
