let rerender_entire_tree = () => {
    console.log("state is changed")
}
let state = {
    Profile_page: {
        posts: [
            {id: 1, message: "Hello Kaneki ", like_count: 3, dislike_count: 1},
            {id: 2, message: "Bye, Feral'", like_count: 3, dislike_count: 1},

        ],
        post_text: "Kaneki Ken"
    },

    Dialogs_page: {
        dialogs_data: [
            {id: 1, name: "Yaroslav"},
            {id: 2, name: "Alex"},
            {id: 3, name: "Veronika"},
            {id: 4, name: "Naruto"},
            {id: 5, name: "Tanjiro"},
            {id: 6, name: "Kaneki"},
        ],
        messages_data: [
            {id: 1, message: "heyy"},
            {id: 2, message: "no"},
            {id: 3, message: "ok"},
            {id: 4, message: "the let's go"},
            {id: 5, message: "yeah that's great"},
            {id: 6, message: "welcome amazing"},
        ]
    }

}
export const add_post = () => {

    let new_post = {
        id: 5,
        message: state.Profile_page.post_text,
        like_count: 0,
        dislike_count: 0
    }
    state.Profile_page.posts.push(new_post);
    state.Profile_page.post_text = "";
    rerender_entire_tree(state);
}
export const update_new_text = (new_text) => {

    state.Profile_page.post_text = new_text;

    rerender_entire_tree(state);
}
export const subscribe = (observer) => {
    rerender_entire_tree = observer;
}
export default state;
