import React from "react";
import styles from "./FormControls.module.css";

export const Textarea = ({input, meta, ...props}) => {
    return(
        <div className={styles.formControl + " " + styles.error}>
            <div>
            <textarea {...input} {...props} />
            </div>
            {meta.error && <span>"some error"</span>}
        </div>
    )
    }
