import React from 'react';
import styles from './users.module.css';
import * as axios from "axios";
import user_photo from '../../assets/griffith.jpg';

class Users extends React.Component {
    componentDidMount() {
        axios.get(`https://social-network.samuraijs.com/api/1.0/users?
        page=${this.props.current_page}
        &count=${this.props.page_size}`)
            .then(response => {
                    this.props.set_users(response.data.items)
                }
            );
    }

    render() {
        let pages_count = Math.ceil(this.props.total_user_count/this.props.page_size);
        let pages = [];
        for (let i=1; i <= pages_count; i++ ){
            pages.push(i);
        }
        return <div>
            {pages.map(p =>{
                return <span className={this.props.current_page === p && styles.selected_page}
                             onClick={()=> {this.props.set_current_page(p)}}>{p}</span>
            })}
            <button onClick={this.get_users}>Get users</button>
            {
                this.props.users.map(u => <div key={u.id}>
                <span>
                    <div>
                        <img className={styles.user_photo} src={u.photos.small != null ? u.photos.small : user_photo}/>
                    </div>
                </span>
                        <span>
                    {u.name}
                            <div>{u.status}</div>
                </span>
                        <span>
                    {u.followed ? <button onClick={() => {
                            this.props.unfollow(u.id)
                        }}>Unfollow</button>
                        : <button onClick={() => {
                            this.props.follow(u.id)
                        }}>Follow</button>}
                </span>
                        <span>
                    <div>{'u.location.city'}</div>
                    <div>{'u.location.country'}</div>
                </span>
                    </div>
                )
            }
        </div>
    }
}

export default Users;
