import {rerender_entire_tree} from "../../render";

let state ={
    Profile_page : {posts : [
            {id: 1, message: "Hello Kaneki ", like_count: 3, dislike_count: 1},
            {id: 2, message: "Bye, Feral'", like_count: 3, dislike_count: 1},

        ]},

    Dialogs_page : {dialogs_data : [
            {id: 1, name: "Yaroslav"},
            {id: 2, name: "Alex"},
            {id: 3, name: "Veronika"},
            {id: 4, name: "Naruto"},
            {id: 5, name: "Tanjiro"},
            {id: 6, name: "Kaneki"},
        ],
        messages_data : [
            {id: 1, message: "heyy"},
            {id: 2, message: "no"},
            {id: 3, message: "ok"},
            {id: 4, message: "the let's go"},
            {id: 5, message: "yeah that's great"},
            {id: 6, message: "welcome amazing"},
        ]}

}
export let add_post = (post_message)=>{

    let new_post = {
        id: 5,
        message: post_message,
        like_count: 0,
        dislike_count: 0
    }
    state.Profile_page.posts.push(new_post);
    rerender_entire_tree();
}
export default state;
