import React from "react";

import My_post from "./My_post/My_post";
import {add_post_action_creator, update_new_text_action_creator} from "../../../redux/state/profile_reducer";
import My_posts from "./My_posts";


function My_posts_container(props) {


    let add_post = () => {
        props.store.dispatch(add_post_action_creator());
    }
    let on_post_change = (text) => {
        let action = update_new_text_action_creator(text);
        props.store.dispatch(action);
    }

    return (<My_posts update_new_text={on_post_change}
                      on_add_post={add_post}
                      posts={props.posts}/>)
}

export default My_posts_container;
