import * as serviceWorker from './serviceWorker';
import store from "./redux/state/state";
import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';


let rerender_entire_tree = (state) => {
    ReactDOM.render(
        <React.StrictMode> <App state={store.get_state()}
                                add_post={store.dispatch.bind(store)}
                                update_new_text={store.update_new_text.bind(store)}/>
        </React.StrictMode>,
        document.getElementById('root')
    );
}

rerender_entire_tree(store.get_state());
store.subscribe(rerender_entire_tree)
// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: https://bit.ly/CRA-PWA
serviceWorker.unregister();
